// TypeScript Version: 4.1

/// <reference types="@stdlib/types"/>

import { Iterator } from '@stdlib/types/iter';

/**
* Return Iterator which have all truthy value after getting first truthy value.
*
* @param iterator - input iterator
* @returns Return Iterator which have all truthy value after getting first truthy value.
*
* @example
* var array2iterator = require( '@stdlib/array/to-iterator' );
*
* var arr = array2iterator( [ false, false, false, true, false ] );
*
* var it = iterCuAny( arr );
*
* var v = it.next().value;
* returns false
*
* v = it.next().value;
* returns false
*
* v = it.next().value;
* returns false
*
* v = it.next().value;
* returns true
*
* v = it.next().value;
* returns true
*
* var bool = it.next().done;
* returns true
*/
declare function iterCuAny( iterator: Iterator ): Iterator;


// EXPORTS //

export = iterCuAny;
